import React, { useState, useRef, useEffect } from "react";

const STARTER = [
  { id: 1, role: "assistant", text: "Hey! I‚Äôm Astro. Ask me anything ‚ú®" },
  {
    id: 2,
    role: "assistant",
    text: "Tip: say 'publish', 'undo', 'small', or 'loader'.",
  },
];

export default function ChatMock({
  onSend = () => {},
  onComposerActive = () => {},
  onAssistantSpot = () => {},
  waitForPulse = async () => {},
  onAssistantDone = () => {},
}) {
  const [messages, setMessages] = useState(STARTER);
  const [input, setInput] = useState("");
  const [pending, setPending] = useState(false);

  const listRef = useRef(null);
  const composerRef = useRef(null);

  useEffect(() => {
    listRef.current?.scrollTo({
      top: listRef.current.scrollHeight,
      behavior: "smooth",
    });
  }, [messages, pending]);

  // Move Astro to composer when user focuses/starts typing
  const reportComposer = () => {
    const rect = composerRef.current?.getBoundingClientRect?.();
    onComposerActive(rect);
  };

  const send = async () => {
    const trimmed = input.trim();
    if (!trimmed || pending) return;

    // user message
    const userMsg = { id: Date.now(), role: "user", text: trimmed };
    setMessages((m) => [...m, userMsg]);
    setInput("");
    onSend(trimmed);

    // show typing bubble
    setPending(true);

    // ensure typing bubble is in DOM, then move Astro there
    await new Promise((r) => requestAnimationFrame(r));
    await new Promise((r) => requestAnimationFrame(r));
    const typing = listRef.current?.querySelector(".bubble.assistant .typing");
    const rect =
      typing?.getBoundingClientRect?.() ??
      listRef.current?.getBoundingClientRect?.();
    onAssistantSpot(rect);

    // run Pulse flourish while "thinking"
    await waitForPulse();

    // now render assistant message
    const reply = mockReply(trimmed);
    const newMsg = { id: Date.now() + 1, role: "assistant", text: reply };
    setMessages((m) => [...m, newMsg]);
    setPending(false);

    // after it paints, send Astro back to composer
    await new Promise((r) => requestAnimationFrame(r));
    const cRect = composerRef.current?.getBoundingClientRect?.();
    onAssistantDone(cRect);
  };

  const onKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      send();
    }
  };

  return (
    <div className="chat">
      <div className="chat-list" ref={listRef}>
        {messages.map((m) => (
          <div key={m.id} className={`bubble ${m.role}`}>
            <div className="bubble-inner">{m.text}</div>
          </div>
        ))}
        {pending && (
          <div className="bubble assistant">
            <div className="bubble-inner typing">
              <span className="dot"></span>
              <span className="dot"></span>
              <span className="dot"></span>
            </div>
          </div>
        )}
      </div>

      <div
        className="composer"
        ref={composerRef}
        onFocusCapture={reportComposer}
        onPointerDownCapture={reportComposer}
      >
        <textarea
          value={input}
          onChange={(e) => {
            setInput(e.target.value);
            reportComposer();
          }}
          onKeyDown={onKeyDown}
          onFocus={reportComposer}
          placeholder="Message Astro‚Ä¶"
          rows={1}
        />
        <button onClick={send}>Send</button>
      </div>
    </div>
  );
}

function mockReply(text) {
  const t = text.toLowerCase();
  if (t.includes("publish")) return "Published! üöÄ";
  if (t.includes("undo")) return "Rolled back. ‚Ü©Ô∏è";
  if (t.includes("small")) return "Going small‚Ä¶ ü´ß";
  if (t.includes("load")) return "Loading sequence‚Ä¶ ‚è≥";
  return "Toggled scale. What next?";
}
