import React, { useRef, useEffect } from "react";
import Astro from "./Astro";
import ChatMock from "./ChatMock";

export default function App() {
  const astroRef = useRef(null);

  const controlsRef = useRef({
    publish: () => {},
    undo: () => {},
    sparkIdea: () => {},
    bored: () => {},
    bigLoader: () => {},
    smallLoader: () => {},
    shrink: () => {},
    pulse: () => {},
    idle: () => {},
    lookAt: () => {}, // we'll call this continuously
  });

  // Hook up continuous mouse tracking → eyes follow (0–100%)
  useEffect(() => {
    let rafId = 0;
    let lastX = window.innerWidth / 2;
    let lastY = window.innerHeight / 2;
    let dirty = true;

    const onMove = (e) => {
      lastX = e.clientX;
      lastY = e.clientY;
      dirty = true;
    };

    const tick = () => {
      if (dirty) {
        const maxW = Math.max(1, window.innerWidth);
        const maxH = Math.max(1, window.innerHeight);
        const nx = (lastX / maxW) * 100;       // 0 → 100
        const ny = 100 - (lastY / maxH) * 100; // invert Y like your snippet
        controlsRef.current.lookAt(nx, ny);
        dirty = false;
      }
      rafId = requestAnimationFrame(tick);
    };

    window.addEventListener("mousemove", onMove, { passive: true });
    rafId = requestAnimationFrame(tick);

    // keep mapping correct if the window resizes
    const onResize = () => { dirty = true; };
    window.addEventListener("resize", onResize);

    return () => {
      window.removeEventListener("mousemove", onMove);
      window.removeEventListener("resize", onResize);
      cancelAnimationFrame(rafId);
    };
  }, []);

  return (
    <div className="app">
      <Astro
        ref={astroRef}
        initialX={260}
        initialY={260}
        zIndex={20000}
        onReady={(controls) => (controlsRef.current = controls)}
      />

      <div className="ui-root" data-ui-root>
        <header className="app-header">
          <div className="brand"><span className="dot" /> Astro Sandbox</div>
          <div className="controls">
            <button onClick={() => controlsRef.current.publish()}>Publish</button>
            <button onClick={() => controlsRef.current.undo()}>Undo</button>
            <button onClick={() => controlsRef.current.sparkIdea()}>Idea Spark</button>
            <button onClick={() => controlsRef.current.bored()}>Boredom</button>
            <button onClick={() => controlsRef.current.bigLoader()}>Big Loader</button>
            <button onClick={() => controlsRef.current.smallLoader()}>Small Loader</button>
            <button onClick={() => controlsRef.current.shrink()}>Shrink</button>
            <button onClick={() => controlsRef.current.pulse()}>Pulse</button>
            <button onClick={() => controlsRef.current.idle()}>Idle</button>
          </div>
        </header>

        <main className="chat-layout">
          <ChatMock
            onAssistantSpot={(rect) => astroRef.current?.moveTo(rect?.left, rect?.top)}
            onAssistantDone={(composerRect) => {
              setTimeout(() => {
                if (composerRect) astroRef.current?.moveTo(composerRect.left, composerRect.top);
              }, 1000);
            }}
            onSend={() => {}}
          />
        </main>
      </div>
    </div>
  );
}
