import React, {
  useEffect,
  useRef,
  useImperativeHandle,
  forwardRef,
  useState,
} from "react";
import { useRive, useStateMachineInput } from "@rive-app/react-canvas";

const STATE_MACHINE_NAME = "State Machine 1";
const RIVE_FILE = "Astro_v5_for_Yael_03.riv";

/** =================== Animation Tuning =================== */
const ANIM = {
  shrinkMs: 450,
  delayBeforeMoveMs: 400,
  travelMs: 1000,
  sway: 80,
  cp1: 0.33,
  cp2: 0.66,
  easing: "cubic-bezier(0.22,1,0.36,1)",
  dotSize: 15,
  dotColor: "#3AA0FF",
  trailCount: 19,
  trailStaggerMs: 1.5,
  trailFade: 0.25,
  trailMinScale: 0.9,
  trailBlurPx: 0.6,
};
/** ======================================================== */

function setMotionPath(el, d) {
  el.style.offsetPath = `path("${d}")`;
  el.style.webkitOffsetPath = `path("${d}")`;
}
function setOffsetDistance(el, v) {
  el.style.offsetDistance = v;
  el.style.webkitOffsetDistance = v;
}
function setOffsetRotate(el, v) {
  el.style.offsetRotate = v;
  el.style.webkitOffsetRotate = v;
}
function setOffsetAnchor(el, v) {
  el.style.offsetAnchor = v;
  el.style.webkitOffsetAnchor = v;
}
function buildPathD(start, end, sway = ANIM.sway, t1 = ANIM.cp1, t2 = ANIM.cp2) {
  const dx = end.x - start.x, dy = end.y - start.y;
  const len = Math.max(1, Math.hypot(dx, dy));
  const nx = -dy / len, ny = dx / len;
  const c1x = start.x + dx * t1 + nx * sway;
  const c1y = start.y + dy * t1 + ny * sway;
  const c2x = start.x + dx * t2 - nx * (sway * 0.65);
  const c2y = start.y + dy * t2 - ny * (sway * 0.65);
  return `M ${start.x},${start.y} C ${c1x},${c1y} ${c2x},${c2y} ${end.x},${end.y}`;
}

const Astro = forwardRef(function Astro(props, ref) {
  const {
    width = 200,
    height = 200,
    initialX = 240,
    initialY = 240,
    zIndex = 9999,
    onReady,
  } = props;

  const wrapperRef = useRef(null);
  const leadDotRef = useRef(null);
  const trailRefs = useRef([]);
  const [center, setCenter] = useState({ x: initialX, y: initialY });
  const [riveHidden, setRiveHidden] = useState(false);

  // Rive
  const { rive, RiveComponent } = useRive({
    src: RIVE_FILE,
    stateMachines: STATE_MACHINE_NAME,
    autoplay: true,
  });

  const publishTrigger = useStateMachineInput(rive, STATE_MACHINE_NAME, "Publish");
  const undoTrigger = useStateMachineInput(rive, STATE_MACHINE_NAME, "Undo");
  const gettingSmallTrigger = useStateMachineInput(rive, STATE_MACHINE_NAME, "Getting_Small");
  const loaderTrigger = useStateMachineInput(rive, STATE_MACHINE_NAME, "Loader");
  const scaleBoolean = useStateMachineInput(rive, STATE_MACHINE_NAME, "Scale_Boolean");

  const sleep = (ms) => new Promise((r) => setTimeout(r, ms));

  async function waitForInputs(timeoutMs = 1500) {
    const startT = performance.now();
    while (
      (!rive || !scaleBoolean || !gettingSmallTrigger) &&
      performance.now() - startT < timeoutMs
    ) {
      await sleep(16);
    }
    return !!(rive && scaleBoolean && gettingSmallTrigger);
  }

  useEffect(() => {
    if (!onReady) return;
    if (rive && publishTrigger && undoTrigger && gettingSmallTrigger && loaderTrigger && scaleBoolean) {
      onReady({
        publish: () => publishTrigger.fire(),
        undo: () => undoTrigger.fire(),
        gettingSmall: () => gettingSmallTrigger.fire(),
        loader: () => loaderTrigger.fire(),
        toggleScale: () => {
          try { scaleBoolean.value = !scaleBoolean.value; } catch {}
        },
      });
    }
  }, [rive, publishTrigger, undoTrigger, gettingSmallTrigger, loaderTrigger, scaleBoolean, onReady]);

  useImperativeHandle(ref, () => ({
    async moveTo(x, y) {
      const ready = await waitForInputs();
      if (!ready) return;

      const lead = leadDotRef.current;
      if (!lead) return;

      // 1) Shrink
      try { scaleBoolean.value = false; } catch {}
      gettingSmallTrigger?.fire();
      await sleep(ANIM.shrinkMs);

      // 2) Hide Rive, show dots
      setRiveHidden(true);
      const start = { x: center.x, y: center.y };
      const end = { x, y };

      lead.style.opacity = "1";
      trailRefs.current.forEach((d, i) => {
        if (!d) return;
        const ratio = (i + 1) / ANIM.trailCount;
        const scale = ANIM.trailMinScale + (1 - ANIM.trailMinScale) * (1 - ratio);
        const opacity = ANIM.trailFade + (1 - ANIM.trailFade) * (1 - ratio);
        d.style.opacity = String(opacity);
        d.style.transform = `scale(${scale})`;
      });

      await sleep(ANIM.delayBeforeMoveMs);

      // 3) Motion path
      const sway = ANIM.sway * (0.85 + Math.random() * 0.3);
      const d = buildPathD(start, end, sway);

      const prepDot = (el) => {
        if (!el) return;
        setMotionPath(el, d);
        setOffsetDistance(el, "0%");
        setOffsetRotate(el, "0deg");
        setOffsetAnchor(el, "50% 50%");
      };
      prepDot(lead);
      trailRefs.current.forEach((td) => td && prepDot(td));

      const leadAnim = lead.animate(
        [{ offsetDistance: "0%" }, { offsetDistance: "100%" }],
        { duration: ANIM.travelMs, easing: ANIM.easing, fill: "forwards" }
      );

      trailRefs.current.forEach((td, i) => {
        if (!td) return;
        td.animate([{ offsetDistance: "0%" }, { offsetDistance: "100%" }], {
          duration: ANIM.travelMs,
          delay: (i + 1) * ANIM.trailStaggerMs,
          easing: ANIM.easing,
          fill: "forwards",
        });
      });

      await leadAnim.finished;

      // 4) Teleport, show Rive again
      lead.style.opacity = "0";
      trailRefs.current.forEach((td) => td && (td.style.opacity = "0"));
      setCenter({ x, y });
      setRiveHidden(false);

      // 5) Grow back
      await new Promise((r) => requestAnimationFrame(() => r()));
      try { scaleBoolean.value = true; } catch {}
    },
  }));

  useEffect(() => {
    const el = wrapperRef.current;
    if (!el) return;
    const left = center.x - width / 2;
    const top = center.y - height / 2;
    el.style.transform = `translate3d(${left}px, ${top}px, 0)`;
  }, [center, width, height]);

  return (
    <>
      {/* Lead dot */}
      <div
        ref={leadDotRef}
        style={{
          position: "fixed",
          left: 0, top: 0,
          width: ANIM.dotSize,
          height: ANIM.dotSize,
          borderRadius: "50%",
          background: ANIM.dotColor,
          pointerEvents: "none",
          zIndex,
          opacity: 0,
          transition: "opacity 120ms ease",
        }}
      />
      {/* Trail dots */}
      {Array.from({ length: ANIM.trailCount }).map((_, i) => (
        <div
          key={i}
          ref={(el) => (trailRefs.current[i] = el)}
          style={{
            position: "fixed",
            left: 0, top: 0,
            width: ANIM.dotSize,
            height: ANIM.dotSize,
            borderRadius: "50%",
            background: ANIM.dotColor,
            pointerEvents: "none",
            zIndex: zIndex - 1,
            opacity: 0,
            transition: "opacity 120ms ease",
            filter: ANIM.trailBlurPx ? `blur(${ANIM.trailBlurPx}px)` : "none",
          }}
        />
      ))}

      {/* Rive */}
      <div
        ref={wrapperRef}
        style={{
          position: "fixed",
          left: 0, top: 0,
          width, height,
          zIndex: zIndex - 2,
          pointerEvents: riveHidden ? "none" : "auto",
          opacity: riveHidden ? 0 : 1,
          transition: "opacity 80ms linear",
          transform: "translate3d(0,0,0)",
        }}
      >
        <RiveComponent style={{ width: "100%", height: "100%" }} />
      </div>
    </>
  );
});

export default Astro;
