// App.js
import React, { useRef, useEffect } from "react";
import Astro from "./Astro";
import ChatMock from "./ChatMock";

export default function App() {
  const astroRef = useRef(null);
  const controlsRef = useRef({
    publish: () => {},
    undo: () => {},
    gettingSmall: () => {},
    loader: () => {},
    toggleScale: () => {},
  });

  useEffect(() => {
    const onPointerDown = (e) => {
      const ui = document.querySelector("[data-ui-root]");
      if (ui && ui.contains(e.target)) return;
      astroRef.current?.moveTo(e.clientX, e.clientY);
    };
    window.addEventListener("pointerdown", onPointerDown, { passive: true });
    return () => window.removeEventListener("pointerdown", onPointerDown);
  }, []);

  // Move to top-left of a rect
  const moveAstroToRect = (rect) => {
    if (!rect) return;
    astroRef.current?.moveTo(rect.left, rect.top);
  };

  // Return to composer (top-left)
  const moveAstroToComposer = (composerRect) => {
    if (!composerRect) return;
    astroRef.current?.moveTo(composerRect.left, composerRect.top);
  };

  return (
    <div className="app">
      <Astro
        ref={astroRef}
        initialX={260}
        initialY={260}
        zIndex={20000} // stays above chat UI
        onReady={(controls) => (controlsRef.current = controls)}
      />

      <div className="ui-root" data-ui-root>
        <header className="app-header">
          <div className="brand"><span className="dot" /> Astro Sandbox</div>
          <div className="controls">
            <button onClick={() => controlsRef.current.publish()}>Publish</button>
            <button onClick={() => controlsRef.current.undo()}>Undo</button>
            <button onClick={() => controlsRef.current.gettingSmall()}>Small</button>
            <button onClick={() => controlsRef.current.loader()}>Loader</button>
            <button onClick={() => controlsRef.current.toggleScale()}>Toggle Scale</button>
          </div>
        </header>

        <main className="chat-layout">
          <ChatMock
            onAssistantSpot={moveAstroToRect}       // move when user sends
            onAssistantDone={moveAstroToComposer}   // return after assistant renders
            onSend={(msg) => {
              const t = msg.toLowerCase();
              if (t.includes("publish")) controlsRef.current.publish();
              else if (t.includes("undo")) controlsRef.current.undo();
              else if (t.includes("small")) controlsRef.current.gettingSmall();
              else if (t.includes("load")) controlsRef.current.loader();
              else controlsRef.current.toggleScale();
            }}
          />
        </main>
      </div>
    </div>
  );
}
